export default "import React, { useState, useEffect } from \"react\";\nimport { withContext as ReactTags } from \"aakansha-react-tag-input\";\nimport styles from \"./ReactTags.module.scss\";\n\nconst KeyCodes = {\n  comma: 188,\n  enter: 13,\n};\n\nconst delimiters = [KeyCodes.comma, KeyCodes.enter];\n\nconst ReactTagsDemo = (props) => {\n  const [tags, setTags] = useState(props.tags ? props.tags : []);\n  /*remove*/\n  useEffect(() => {\n    setTags(props.tags ? props.tags : []);\n  }, [props.tags]);\n  /*remove*/\n\n  const handleDelete = (i) => {\n    setTags(tags.filter((tag, index) => index !== i));\n  };\n\n  const handleAddition = (tag) => {\n    setTags([...tags, tag]);\n    if (props.updateStyleHover) props.updateStyleHover();\n  };\n\n  const handleDrag = (tag, currPos, newPos) => {\n    const newTags = [...tags].slice();\n\n    newTags.splice(currPos, 1);\n    newTags.splice(newPos, 0, tag);\n\n    setTags(newTags);\n  };\n\n  const handleTagClick = (index) => {\n    console.log(\"The tag at index \" + index + \" was clicked\");\n  };\n\n  const onClearAll = () => {\n    setTags([]);\n  };\n\n  const onTagUpdate = (i, newTag) => {\n    const updatedTags = tags.slice();\n    updatedTags.splice(i, 1, newTag);\n    setTags(updatedTags);\n  };\n\n  return (\n    <div className={styles.ReactTags}>\n      <ReactTags\n        handleDelete={handleDelete}\n        handleAddition={handleAddition}\n        handleDrag={handleDrag}\n        delimiters={delimiters}\n        handleTagClick={handleTagClick}\n        onClearAll={onClearAll}\n        onTagUpdate={onTagUpdate}\n        {...props}\n        tags={tags}\n      />\n    </div>\n  );\n};\nexport default ReactTagsDemo;";